import './App.css';

import React, { Component } from 'react'
import Navbar from './Components/Navbar';
import News from './Components/News';
import {
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";
import Footer from './Components/Footer';
import LoadingBar from 'react-top-loading-bar';


export default class App extends Component {
  pageSize = 8;
  state = {
    progress : 10
  }

  setProgress = (progress) => {
    this.setState({progress: progress})
  }
  render() {
    return (
      <div>
        <Router>
          <Navbar />
          <LoadingBar
            height={3}
            color='#f11946'
            progress={this.state.progress}

          />
          <Routes>
            <Route exact path="/" element={<News setProgress={this.setProgress}   key="genral" pageSize={this.pageSize} counry="in" category="general" />}></Route>
            <Route exact path="/business" element={<News setProgress={this.setProgress}   key="business" pageSize={this.pageSize} counry="in" category="business" />}></Route>
            <Route exact path="/entertainment" element={<News setProgress={this.setProgress}   key="entertainment" pageSize={this.pageSize} counry="in" category="entertainment" />}></Route>
            <Route exact path="/health" element={<News setProgress={this.setProgress}   key="health" pageSize={this.pageSize} counry="in" category="health" />}></Route>
            <Route exact path="/science" element={<News setProgress={this.setProgress}   key="science" pageSize={this.pageSize} counry="in" category="science" />}></Route>
            <Route exact path="/sports" element={<News setProgress={this.setProgress}   key="sports" pageSize={this.pageSize} counry="in" category="sports" />}></Route>
            <Route exact path="/technology" element={<News setProgress={this.setProgress}   key="technology" pageSize={this.pageSize} counry="in" category="technology" />}></Route>
          </Routes>

          <Footer/>
        </Router>
      </div>
    )
  }
}
