import './App.css';

import React, { Component } from 'react'
import Navbar from './Components/Navbar';
import News from './Components/News';
import {
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";
import Footer from './Components/Footer';
import LoadingBar from 'react-top-loading-bar'



export default class App extends Component {
  pageSize = 8;
  render() {
    return (
      <div>
        <Router>
          <Navbar />
          <LoadingBar
            color='#f11946'
            progress={progress}
            onLoaderFinished={() => setProgress(0)}
          />
          <Routes>
            <Route exact path="/" element={<News key="genral" pageSize={this.pageSize} counry="in" category="general" />}></Route>
            <Route exact path="/business" element={<News key="business" pageSize={this.pageSize} counry="in" category="business" />}></Route>
            <Route exact path="/entertainment" element={<News key="entertainment" pageSize={this.pageSize} counry="in" category="entertainment" />}></Route>
            <Route exact path="/health" element={<News key="health" pageSize={this.pageSize} counry="in" category="health" />}></Route>
            <Route exact path="/science" element={<News key="science" pageSize={this.pageSize} counry="in" category="science" />}></Route>
            <Route exact path="/sports" element={<News key="sports" pageSize={this.pageSize} counry="in" category="sports" />}></Route>
            <Route exact path="/technology" element={<News key="technology" pageSize={this.pageSize} counry="in" category="technology" />}></Route>
          </Routes>

          <Footer/>
        </Router>
      </div>
    )
  }
}
