import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spiner from './Spiner';
import PropTypes from 'prop-types'
import InfiniteScroll from 'react-infinite-scroll-component';

export class News extends Component {

  static defaultProps = {
    country: 'in',
    pageSize: 8,
    category: "general"
  }

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string
  }

  Capitalize(str){
    return str.charAt(0).toUpperCase() + str.slice(1);
  }
  constructor(props) {
    super(props);
    this.state = {
      articals: [],
      loading: true,
      page: 1,
      totalResults: 0
    }
    document.title = `${this.Capitalize(this.props.category)} - NewsThief`;
  }

  async updateNews() {
    this.props.setProgress(10);
    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=68023b1d07ec452db33798367fb4b254&page=${this.state.page}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });
    let data = await fetch(url);
    this.props.setProgress(30);
    let parsedData = await data.json();
    this.props.setProgress(70);
    this.setState({
      articals: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading: true
    });
    this.props.setProgress(100);
    
  }

  // ---------------- when first time page load
  async componentDidMount() {
    this.updateNews();
  }
  
  fetchMoreData = async () => {
    this.setState({page: this.state.page + 1})

    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=68023b1d07ec452db33798367fb4b254&page=${this.state.page}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      articals: this.state.articals.concat(parsedData.articles),
      totalResults: parsedData.totalResults,
      loading: false
    });
  };

  render() {
    return (
      <>
        <h1 className='text-center my-4'>NewsThief - Top {this.Capitalize(this.props.category)} Headlines</h1>
        {/* {this.state.loading && <Spiner />} */}
        <InfiniteScroll
          dataLength={this.state.articals.length}
          next={this.fetchMoreData}
          hasMore={this.state.articals.length !== this.state.totalResults}
          loader={ this.state.loading && <Spiner />}
        >
          <div className="container">
              <div className="row">
                {this.state.articals.map((element, index) => {
                  return <div className="col-md-4" key={index}>
                    <NewsItem title={element.title ? element.title.slice(0, 45) : ""} description={element.description ? element.description.slice(0, 88) : ""} imageurl={element.urlToImage} newsurl={element.url} author={element.author} 
                      date={element.publishedAt} source={element.source.name}/>
                  </div>
                })}
              </div>
          </div>
        </InfiniteScroll>
       
      </>
    )
  }
}

export default News
