import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spiner from './Spiner';
import PropTypes from 'prop-types'


export class News extends Component {

  static defaultProps = {
    country: 'in',
    pageSize: 8,
    category: "genral"
  }

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string
  }

  constructor(props) {
    super(props);
    this.state = {
      articals: [],
      loading: false,
      page: 1
    }
    document.title = `${this.props.category} - NewsThief`;
  }

  async updateNews() {
    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=68023b1d07ec452db33798367fb4b254&page=${this.state.page}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      articals: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading: false
    });
  }

  // ---------------- when first time page load
  async componentDidMount() {
    this.updateNews();
  }
  // -------------------- Prev News
  handelPrevClick = async () => {
    this.setState({ page: this.state.page - 1 });
    this.updateNews();
  }

  // -------------------- Next News
  handelNextClick = async () => {
    this.setState({ page: this.state.page + 1 });
    this.updateNews();
  }

  render() {
    return (
      <div className='container my-3'>
        <h1 className='text-center my-4'>NewsThief - Top Headlines</h1>
        {this.state.loading && <Spiner />}
        <div className="row">
          {!this.state.loading && this.state.articals.map((element) => {
            return <div className="col-md-4" key={element.url}>

              {/* call news Item componet */}
              <NewsItem title={element.title ? element.title.slice(0, 45) : ""} description={element.description ? element.description.slice(0, 88) : ""}
                imageurl={element.urlToImage} newsurl={element.url} author={element.author} date={element.publishedAt}
                source={element.source.name} />
            </div>
          })}
        </div>
        <div className="container d-flex justify-content-between">
          <button disabled={this.state.page <= 1} type="button" className="btn btn-dark" onClick={this.handelPrevClick}>Prev</button>
          <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults / this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.handelNextClick}>Next</button>
        </div>
      </div>
    )
  }
}

export default News
