import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
  
  constructor() {
    super();
    // console.log("constructor");
    this.state = {
      articals: [],
      loading: false,
      page: 1
    }
  }

  async componentDidMount() {
    // console.log("cdm");
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=68023b1d07ec452db33798367fb4b254&page=1&&pageSize=20";
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({articals: parsedData.articles, totalResults: parsedData.totalResults });
  }

  handelPrevClick = async () => {
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=68023b1d07ec452db33798367fb4b254&page=${this.state.page - 1}&pageSize=20`;
    let data = await fetch(url);
    let parsedData = await data.json();
    
      this.setState({
        page: this.state.page - 1,
        articals: parsedData.articles
      })
  }
  handelNextClick = async () => {
    alert(this.state.page + 1)
    alert(Math.ceil(this.state.totalResults / 20))

    if (this.state.page + 1 > Math.ceil(this.state.totalResults / 20)) {

    } else {
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=68023b1d07ec452db33798367fb4b254&page=${this.state.page + 1}&pageSize=20`;
        let data = await fetch(url);
        let parsedData = await data.json();

        this.setState({
          page: this.state.page + 1,
          articals: parsedData.articles
        })
    }
    
  }

  render() {
    return (
      <div className='container my-3'>
        <h2>NewsThief - Top Headlines</h2>
        <div className="row">
          {this.state.articals.map((element)=>{
            return <div className="col-md-4"  key={element.url}>
                    <NewsItem title={element.title ? element.title.slice(0, 45): ""} description={element.description ? element.description.slice(0, 88):""} 
                    imageurl={element.urlToImage} newsurl={element.url} />
                  </div>
          })}
        </div>
        <div className="container d-flex justify-content-between">
        <button disabled={this.state.page<=1} type="button" class="btn btn-dark" onClick={this.handelPrevClick}>Prev</button>
        <button type="button" class="btn btn-dark" onClick={this.handelNextClick}>Next</button>
        </div>
      </div>
    )
  }
}

export default News
